SELECT f.apply_no
FROM (SELECT
        d.apply_no,
        e.outside_status
      FROM (SELECT
              a.apply_no,
              b.handle_time,matter_key
            FROM biz_apply_order a INNER JOIN biz_order_matter_record b
                ON a.apply_no = b.apply_no AND (a.partner_insurance_id LIKE  'XDA-WC001')
            WHERE (b.matter_key in ('returnConfirm', 'TransferOut', 'MortgagePass')) AND
                  b.handle_time >= date_sub(curdate(), INTERVAL 1 DAY)) d LEFT JOIN biz_after_loan_material e
          ON d.apply_no = e.apply_no and d.matter_key = e.node_id) f
WHERE f.outside_status != 1 OR f.outside_status IS NULL;
